

module.exports = function (min, next) {

	var min   = min;
	var next  = next;
	var times = 0;

	return function Min (event) {

		event.route ({
			name   : 'Min',
			min    : min,
			times  : times,
			filter : (times >= min),
			next   : next.name
		});

		if (times >= min) {
			next (event);
		}
		else times ++;
	};
};